import React, { useState } from "react";
import "./App.css";
import CustomForm from "./components/Form/CustomForm";

export default class App extends React.Component {
  constructor(props) {
    super(props);
  }

  state = {
    submitted: false,
  };

  setSubmitted = (isSubmitted) => {
    this.setState({ isSubmitted });
  };

  render() {
    return (
      <>
        {!this.state.submitted && <CustomForm submitForm={this.setSubmitted} />}
        {this.state.submitted && <p>You submitted the form!</p>}
      </>
    );
  }
}

