import React, { useEffect, useState } from "react";
import { Formik, Form } from "formik";
import * as Yup from "yup";
import CustomTextInput from "./FormComponents/CustomTextInput";
import CountrySelect from "./FormComponents/CountrySelect";
import { Styles } from "../../Styles";

const phoneRegExp = /^((\\+[1-9]{1,4}[ \\-]*)|(\\([0-9]{2,3}\\)[ \\-]*)|([0-9]{2,4})[ \\-]*)*?[0-9]{3,4}?[ \\-]*[0-9]{3,4}?$/;

export default class CustomForm extends React.Component {
  constructor(props) {
    super(props);
  }
  state = {
    loading: true,
    countries: null,
    countryCode: null,
  };

  async componentDidMount() {
    const url = "https://restcountries.eu/rest/v2/all";
    const response = await fetch(url);
    const data = await response.json();
    this.setState({ countries: data });
  }

  setCountryCode = (newCountryCode) => {
    this.setState({ newCountryCode });
  };

  render() {
    return (
      <Styles>
        <div>
          <h1>Here's a Form!</h1>
          <CustomTextInput
            label="First Name"
            name="firstName"
            type="text"
            placeholder="First Name"
          />
          <br />
          <CustomTextInput
            label="Middle Name"
            name="middleName"
            type="text"
            placeholder="Middle Name"
          />
          <br />
          <CustomTextInput
            label="Last Name"
            name="lastName"
            type="text"
            placeholder="Last Name"
          />
          <br />
          <CountrySelect
            label="Country"
            name="country"
            countries={this.state.countries}
            setCountryCode={this.setCountryCode}
          />
          <br />
          <CustomTextInput
            label="Phone Number"
            name="phoneNumber"
            type="text"
            placeholder=""
          />
          <br />
          <CustomTextInput
            label="Email Address"
            name="emailAddress"
            type="text"
            placeholder=""
          />
          <br />
          <CustomTextInput
            label="Favorite Color"
            name="favoriteColor"
            type="text"
            placeholder=""
          />
          <br />
          <CustomTextInput
            label="Comments"
            name="comments"
            type="text"
            placeholder=""
          />
        </div>
      </Styles>
    );
  }
}
