import React, { useState } from "react";
import * as _ from "underscore";

import { setNestedObjectValues, useField } from "formik";

const CountrySelect = ({ label, ...props }) => {
  const [field, meta] = useField(props);

  const [selectedCountry, setSelectedCountry] = useState("");
  const [selectedFlag, setSelectedFlag] = useState("");

  const onSelectCountry = (event) => {
    console.log(event);
    setSelectedCountry(event.target.value);
    setSelectedFlag(
      _.where(props.countries, { name: event.target.value })[0].flag
    );
  };

  return (
    <>
      <label htmlFor={props.id || props.name}>{label}</label>
      <select onChange={onSelectCountry} value={selectedCountry}>
        <option>Select Country</option>
        {props.countries.map((country) => (
          <option {...field} key={country.name} value={country.name}>
            {country.name}
          </option>
        ))}
      </select>
      {meta.touched && meta.error ? <div>{meta.error}</div> : null}
      {selectedFlag && <img src={selectedFlag} alt={selectedCountry} />}
    </>
  );
};

export default CountrySelect;
