import React, { useState } from "react";
import * as _ from "underscore";
import CustomSelect from "./CustomSelect";

import { useField } from "formik";

const CountrySelect = ({ label, ...props }) => {
  const [field, meta] = useField(props);

  const [selectedCountry, setSelectedCountry] = useState(null);
  const [selectedFlag, setSelectedFlag] = useState(null);

  const onSelectCountry = (country) => {
    setSelectedCountry(country);
    setSelectedFlag(_.where(props.countries, { name: country })[0].flag);
    props.setCountryCode({
      countryCode: _.where(props.countries, { name: country })[0].callingCodes,
    });
    field.value = country;
  };

  return (
    <>
      <label htmlFor={props.id || props.name}>{label}</label>
      {/* <select
        {...field}
        {...props}
        onChange={onSelectCountry}
        value={selectedCountry}
      > */}
      <CustomSelect label={label} name="country">
        <option>Select Country</option>
        {props.countries.map((country) => (
          <option
            key={country.name}
            value={country.name}
            onClick={() => {
              onSelectCountry(country.name);
            }}
          >
            {country.name}
          </option>
        ))}
      </CustomSelect>
      {/* </select> */}
      {meta.touched && meta.error ? <div>{meta.error}</div> : null}
      {selectedFlag && <img src={selectedFlag} alt={selectedCountry} />}
    </>
  );
};

export default CountrySelect;
