import React, { useEffect, useState } from "react";
import { Formik, Form } from "formik";
import * as Yup from "yup";
import CustomTextInput from "./FormComponents/CustomTextInput";
import CountrySelect from "./FormComponents/CountrySelect";
import ColorSelect from "./FormComponents/ColorSelect";
import { Styles } from "../../Styles";

const phoneRegExp = /^((\\+[1-9]{1,4}[ \\-]*)|(\\([0-9]{2,3}\\)[ \\-]*)|([0-9]{2,4})[ \\-]*)*?[0-9]{3,4}?[ \\-]*[0-9]{3,4}?$/;

export default class CustomForm extends React.Component {
  constructor(props) {
    super(props);
  }
  state = {
    loading: true,
    countries: null,
    countryCode: null,
    initialValues: {
      firstName: "",
      middleName: "",
      lastName: "",
      country: "",
      phoneNumber: "",
      emailAddress: "",
      favoriteColor: "",
      comments: "",
    },
    initialValuesSet: false,
  };

  async componentDidMount() {
    const url = "https://restcountries.eu/rest/v2/all";
    const response = await fetch(url);
    const data = await response.json();
    this.setState({ countries: data });

    this.setInitialValues();
  }

  setCountryCode = (newCountryCode) => {
    this.setState({ newCountryCode });
  };

  setInitialValues() {
    if (this.props.submittedValues) {
      this.setState({
        initialValues: {
          firstName: this.props.submittedValues.firstName,
          middleName: this.props.submittedValues.middleName
            ? this.props.submittedValues.middleName
            : "",
          lastName: this.props.submittedValues.lastName,
          country: this.props.submittedValues.country,
          phoneNumber: this.props.submittedValues.phoneNumber,
          emailAddress: this.props.submittedValues.emailAddress,
          favoriteColor: this.props.submittedValues.favoriteColor,
          comments: this.props.submittedValues.comments
            ? this.props.submittedValues.comments
            : "",
        },
      });
    }
    this.setState({ initialValuesSet: true });
  }

  render() {
    return (
      <Styles>
        <div>
          {this.state.countries && this.state.initialValuesSet && (
            <Formik
              initialValues={this.state.initialValues}
              validationSchema={Yup.object({
                firstName: Yup.string(),
                //   .required("First Name is required"),
                middleName: Yup.string().notRequired(),
                lastName: Yup.string(),
                //   .required("Last Name is required"),
                country: Yup.string(),
                //   .required("Country is required"),
                phoneNumber: Yup.string()
                  .matches(phoneRegExp, "Phone number is not valid")
                  .required("Phone Number is required"),
                emailAddress: Yup.string().email("Invalid Email Address"),
                //   .required("Email Address is required"),
                favoriteColor: Yup.string(),
                //   .required("Favorite Color is required"),
                comments: Yup.string().notRequired(),
              })}
              onSubmit={(values, { setSubmitting, resetForm }) => {
                setTimeout(() => {
                  resetForm();
                  setSubmitting(false);
                }, 3000);
                this.props.submitForm();
                this.props.submitValues(values);
              }}
            >
              {(props) => (
                <Form>
                  <h1>Here's a Form!</h1>
                  <CustomTextInput
                    label="First Name"
                    name="firstName"
                    type="text"
                    placeholder="First Name"
                  />
                  <br />
                  <CustomTextInput
                    label="Middle Name"
                    name="middleName"
                    type="text"
                    placeholder="Middle Name"
                  />
                  <br />
                  <CustomTextInput
                    label="Last Name"
                    name="lastName"
                    type="text"
                    placeholder="Last Name"
                  />
                  <br />
                  <CountrySelect
                    label="Country"
                    name="country"
                    countries={this.state.countries}
                    setCountryCode={this.setCountryCode}
                  >
                    <option>Select Country</option>
                    {props.countries.map((country) => (
                      <option key={country.name} value={country.name}>
                        {country.name}
                      </option>
                    ))}
                  </CountrySelect>
                  <br />
                  <CustomTextInput
                    label="Phone Number"
                    name="phoneNumber"
                    type="text"
                    placeholder=""
                  />
                  <br />
                  <CustomTextInput
                    label="Email Address"
                    name="emailAddress"
                    type="text"
                    placeholder=""
                  />
                  <br />
                  <ColorSelect
                    label="Favorite Color"
                    name="favoriteColor"
                    placeholder=""
                  />
                  <br />
                  <CustomTextInput
                    label="Comments"
                    name="comments"
                    type="text"
                    placeholder=""
                  />
                  <br />
                  <button type="submit">
                    {props.isSubmitting ? "Loading..." : "Submit"}
                  </button>
                </Form>
              )}
            </Formik>
          )}
        </div>
      </Styles>
    );
  }
}
