import React, { useState } from "react";
import "./App.css";
import CustomForm from "./components/Form/CustomForm";
import SubmittedForm from "./components/Form/SubmittedForm";

export default class App extends React.Component {
  constructor(props) {
    super(props);
  }

  state = {
    submitted: false,
    submittedValues: null,
  };

  setSubmitted = () => {
    this.setState({ submitted: true });
  };

  setSubmittedValues = (values) => {
    this.setState({ submittedValues: values });
    console.log(values);
  };

  render() {
    return (
      <>
        {!this.state.submitted && (
          <CustomForm
            submitForm={this.setSubmitted}
            submitValues={this.setSubmittedValues}
          />
        )}
        {this.state.submitted && this.state.submittedValues && (
          <SubmittedForm submittedValues={this.state.submittedValues} />
        )}
      </>
    );
  }
}

// export default App;
