import React from "react";
import NameField from "./FormFields/NameField";
import { Formik, useField, Form } from "formik";
import * as Yup from "yup";
import { string } from "yup/lib/locale";

const CustomTextInput = ({ label, ...props }) => {
    const [field, meta] = useField(props);

    return (
        <>
            <label htmlFor={props.id || props.name}>{label}</label>
            <input className="text-input" {...field} {...props} />
            {meta.touched && meta.error ? (
                <div>{ meta.error }</div>) : null
            }
        </>
    )
}

const CustomSelect = ({ label, ...props }) => {
    const [field, meta] = useField(props);

    return (
        <>
            <label htmlFor={props.id || props.name}>{label}</label>
            <select {...field} {...props} />
            {meta.touched && meta.error ? (
                <div>{ meta.error }</div>) : null
            }
        </>
    )
}

const phoneRegExp = /^((\\+[1-9]{1,4}[ \\-]*)|(\\([0-9]{2,3}\\)[ \\-]*)|([0-9]{2,4})[ \\-]*)*?[0-9]{3,4}?[ \\-]*[0-9]{3,4}?$/

const CustomForm = () => {
  return (
    <div>
      <h1>Here is a form</h1>
          <NameField />
          <Formik
              initialValues={{
                  firstName:'',
                  middleName:'',
                  lastName:'',
                  country:'',
                  phoneNumber:null,
                  emailAddress:'',
                  favoriteColor:'',
                  comments:''
              }}
              validationSchema={Yup.object({
                  firstName: Yup.string()
                    .required("First Name is required"),
                  middleName: Yup.string()
                    .notRequired(),
                  lastName: Yup.string()
                    .required("Last Name is required"),
                  country: Yup.string()
                    .required("Country is required"),
                  phoneNumber: Yup.string()
                      .matches(phoneRegExp, 'Phone number is not valid')
                      .required("Phone Number is required"),
                  emailAddress: Yup.string()
                      .email("Invalid Email Address"),
                  favoriteColor: Yup.string()
                      .required("Favorite Color is required"),
                  comments: Yup.string()
                  .notRequired()
              })}
              onSubmit={(values, { setSubmitting, resetForm }) => {
                  setTimeout(() => {
                      alert(JSON.stringify(values, null, 2));
                      resetForm();
                      setSubmitting(false);
                  }, 3000)
              }}
          >
              {props => (
                  <Form>
                      <h1>Here's a Form!</h1>
                      <CustomTextInput label="First Name" name="firstName" type="text" placeholder="First Name" />
                      <CustomTextInput label="Middle Name" name="middleName" type="text" placeholder="Middle Name" />
                      <CustomTextInput label="Last Name" name="lastName" type="text" placeholder="Last Name" />
                      <CustomSelect label="Country" name="country" />
                      <CustomTextInput label="Phone Number" name="phoneNumber" type="text" placeholder="" />
                      <CustomTextInput label="Email Address" name="emailAddress" type="text" placeholder="" />
                      <CustomTextInput label="Favorite Color" name="favoriteColor" type="text" placeholder="" />
                      <CustomTextInput label="Comments" name="comments" type="text" placeholder=""/>
                  </Form>
              )}
          </Formik>
    </div>
  );
};

export default CustomForm;
