import React, { useEffect, useState } from "react";
import { Formik, Form } from "formik";
import * as Yup from "yup";
import CustomTextInput from "./FormComponents/CustomTextInput";
import CountrySelect from "./FormComponents/CountrySelect";
import { Styles } from "../../Styles";

const phoneRegExp = /^((\\+[1-9]{1,4}[ \\-]*)|(\\([0-9]{2,3}\\)[ \\-]*)|([0-9]{2,4})[ \\-]*)*?[0-9]{3,4}?[ \\-]*[0-9]{3,4}?$/;

export default class CustomForm extends React.Component {
  constructor(props) {
    super(props);
  }
  state = {
    loading: true,
    countries: null,
    countryCode: null,
    initialValues: {
      firstName: "",
      middleName: "",
      lastName: "",
      country: "",
      phoneNumber: "",
      emailAddress: "",
      favoriteColor: "",
      comments: "",
    },
  };

  async componentDidMount() {
    const url = "https://restcountries.eu/rest/v2/all";
    const response = await fetch(url);
    const data = await response.json();
    this.setState({ countries: data });

    if (this.props.submittedValues) {
      this.setInitialValues();
    }
  }

  setCountryCode = (newCountryCode) => {
    this.setState({ newCountryCode });
  };

  setInitialValues() {
    console.log(this.props.submittedValues);
    this.setState({
      initialValues: {
        firstName: this.props.submittedValues.firstName,
      },
    });
  }

  render() {
    return (
      <Styles>
        <div>
          {this.state.countries && (
            <Formik
              initialValues={this.state.initialValues}
              validationSchema={Yup.object({
                firstName: Yup.string(),
                //   .required("First Name is required"),
                middleName: Yup.string().notRequired(),
                lastName: Yup.string(),
                //   .required("Last Name is required"),
                country: Yup.string(),
                //   .required("Country is required"),
                phoneNumber: Yup.string().matches(
                  phoneRegExp,
                  "Phone number is not valid"
                ),
                //   .required("Phone Number is required"),
                emailAddress: Yup.string().email("Invalid Email Address"),
                //   .required("Email Address is required"),
                favoriteColor: Yup.string(),
                //   .required("Favorite Color is required"),
                comments: Yup.string().notRequired(),
              })}
              onSubmit={(values, { setSubmitting, resetForm }) => {
                setTimeout(() => {
                  //   alert(JSON.stringify(values, null, 2));
                  //   resetForm();
                  setSubmitting(false);
                }, 3000);
                this.props.submitForm();
                this.props.submitValues(values);
              }}
            >
              {(props) => (
                <Form>
                  <h1>Here's a Form!</h1>
                  <CustomTextInput
                    label="First Name"
                    name="firstName"
                    type="text"
                    placeholder="First Name"
                  />
                  <br />
                  <CustomTextInput
                    label="Middle Name"
                    name="middleName"
                    type="text"
                    placeholder="Middle Name"
                  />
                  <br />
                  <CustomTextInput
                    label="Last Name"
                    name="lastName"
                    type="text"
                    placeholder="Last Name"
                  />
                  <br />
                  <CountrySelect
                    label="Country"
                    name="country"
                    countries={this.state.countries}
                    setCountryCode={this.setCountryCode}
                  />
                  <br />
                  <CustomTextInput
                    label="Phone Number"
                    name="phoneNumber"
                    type="text"
                    placeholder=""
                  />
                  <br />
                  <CustomTextInput
                    label="Email Address"
                    name="emailAddress"
                    type="text"
                    placeholder=""
                  />
                  <br />
                  <CustomTextInput
                    label="Favorite Color"
                    name="favoriteColor"
                    type="text"
                    placeholder=""
                  />
                  <br />
                  <CustomTextInput
                    label="Comments"
                    name="comments"
                    type="text"
                    placeholder=""
                  />
                  <br />
                  <button type="submit">
                    {props.isSubmitting ? "Loading..." : "Submit"}
                  </button>
                </Form>
              )}
            </Formik>
          )}
        </div>
      </Styles>
    );
  }
}
